changeMessage() {
  this.setState({
   message: "merci pour votre click"
  })
}
  render () {
  return (
    <div className="App-header">
   <h1>{this.state.message}</h1>
   <button onClick={()=> this.changeMessage()}>Shows</button>
    </div>
  );
  } 
}
export default App;
