import './App.css';
import React from 'react';
import photoprofil from "./Images/photoprofil.jpg"
import bouteille from "./Images/bouteille.jpg"
class App extends React.Component {
  constructor(){
  super()
  this.state ={fullName:"Yassine Maaoui",profession: "Avocat",
    bio :"Diplomé d'une licence en droit privée en 2013 j'ai réussis le concours d'entré à l'institut supérieur à la profession d'avocat ou j'ai passé 2 ans d'étude qui ont aboutit à l'obtention du certificat d'aptitude à la profession d'avocat avec laquelle j'ai pu m'inscrire en tant qu'avocat l'ordre national d'avocat de tunisie.",
    imgSrc: photoprofil,
    }
  }

changeMessage() {
  this.setState({
   imgSrc: bouteille
  })
}
  render () {
  return (
    <div className="App-header">
   <img style={{width:230,alignItems:"start"}} src ={this.state.imgSrc}/>
   <button onClick={()=> this.changeMessage()}>Shows</button>
    </div>
  );
  } 
}
export default App;
